import React from 'react';
import './ItemDetail.css';
import Comment from './Comment'
import { connect } from 'react-redux'
import * as actions from './../../actions/index'
class ItemDetail extends React.Component {
    componentDidMount() {
        var id = this.props.match.params.id
        this.props.productShow(id);
        console.log(this.props.product)
    }
    render() {
        let product = this.props.product;
        return (
            <div className="ItemDetail">
                <div className="card card-detail border-light">
                    <div className="container-fliud">
                        <div className="wrapper row">
                            <Preview img={product.image}/>
                            <DetailInfo product={product}/>
                        </div>
                    </div>
                </div>
                <Comment />
            </div>

        );
    }
}
class Preview extends React.Component {
    render() {
        return (
            <div className="preview col-md-6">
                <div className="preview-pic tab-content">
                    <div className="tab-pane active" id="pic-1"><img alt="abc" src={this.props.img} /></div>
                </div>
            </div>
        );
    }
}
class DetailInfo extends React.Component {
    addToCart =() =>
    {
        var Cart = []
        let object = {
            id:this.props.product.id,
            name:this.props.product.name,
            quantity:1,
            price:this.props.product.price
        }
        if( JSON.parse(localStorage.getItem('cartItem')) === null ){Cart.push(object)}
        else {
            Cart = JSON.parse(localStorage.getItem('cartItem'))
            console.log(Cart)
            let item = Cart.some(item => {return item.name===object.name?item:false})
            console.log(item)
            // if(item === false)Cart.push(object)
            // else item.quantity++
        }
        console.log(Cart)
        // let item = Cart.some()
        // if(Cart.some(item =>{item.name ===object.name?true:false}))
        // {
        //     Cart.find(item =>{if(item.name===object.name)item.quantity=item.quantity+object.quantity})
        // }
        // else Cart.push(object)
        // let mCart = Cart.valueOf(object)
        // if(mCart === null) Cart.push(object)
        // else mCart.quantity+= object.quantity
        localStorage.setItem('cartItem',JSON.stringify(Cart));
    }
    render() {
        let { product } = this.props   
        return (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
            <div className="details col-md-6">
                <p className="h2">{product.name}</p>
                <div className="rating">
                    <div className="stars">
                        {

                        }
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star checked"></span>
                        <span className="fa fa-star"></span>
                        <span className="fa fa-star"></span>
                    </div>
                    <span className="review-no">41 reviews</span>
                </div>
                <p className="product-description">{product.description}</p>
                <h4 className="price">current price: <span>${product.price}</span></h4>
                <p className="vote"><strong>91%</strong> of buyers enjoyed this product! <strong>(87 votes)</strong></p>
                <div className="action">
                    <button className="btn btn-success mx-1" type="button" onClick = {this.addToCart}>Add to cart</button>
                    <button className="btn btn-danger" type="button"><span className="fa fa-heart"></span></button>
                </div>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    return {
        product: state.products
    }
}
const mapDispatchToProps = (dispatch, props) => {
    return {
        productShow: (id) => {
            dispatch(actions.productShowRequest(id))
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ItemDetail);
